query:
fragment badge on BadgeNode {
  id
  backgroundColor
  badgeType
  color
  text
  __typename
}
fragment image on ImageNode {
  id
  alt
  height
  src
  width
  __typename
}
fragment instagram on InstagramNode {
  id
  image {
    ...image
    __typename
  }
  instagramProducts {
    edges {
      node {
        id
        faceout {
          variant {
            inventoryQuantity
            shopifyId
            __typename
          }
          status
          __typename
        }
        xPosition
        yPosition
        product {
          absoluteUrl
          status
          title
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  status
  userHandle
  __typename
}
fragment variant on VariantNode {
  available
  compareAtPrice
  finalSale
  id
  inventoryQuantity
  price
  shopifyId
  sku
  __typename
}
fragment video on VideoNode {
  id
  adminTitle
  autoplay
  controls
  loop
  muted
  poster {
    ...image
    __typename
  }
  src: srcUrl
  thumbnail {
    ...image
    __typename
  }
  track: subtitleUrl
  title
  __typename
}
query GetProduct($handle: String, $shopifyId: String) {
  productShopify(handle: $handle, shopifyId: $shopifyId) {
    id
    absoluteUrl
    attributeGroups: productAttributeGroups {
      edges {
        node {
          id
          label
          attribute1Value
          attribute1Url
          attribute2Value
          attribute2Url
          __typename
        }
        __typename
      }
      __typename
    }
    breadcrumbs {
      title
      url
      __typename
    }
    colorways(status: 
"PUBLISHED") {
      edges {
        node {
          handle
          id
          assets(type_In: 
"VIEWS,DETAILS,TECHNICAL_DETAILS") {
            edges {
              node {
                id
                image {
                  ...image
                  description
                  title
                  __typename
                }
                type
                video {
                  ...video
                  __typename
                }
                __typename
              }
              __typename
            }
            __typename
          }
          badge {
            ...badge
            __typename
          }
          color
          colorwayModels {
            id
            model {
              name
              height
              __typename
            }
            size
            __typename
          }
          sizesAvailable {
            available
            size
            __typename
          }
          status
          styledWith {
            colorway {
              id
              color
              __typename
            }
            product {
              id
              absoluteUrl
              colorways(status: 
"PUBLISHED") {
                edges {
                  node {
                    id
                    image {
                      ...image
                      __typename
                    }
                    color
                    status
                    variantStacks {
                      id
                      size
                      variant {
                        ...variant
                        __typename
                      }
                      __typename
                    }
                    __typename
                  }
                  __typename
                }
                __typename
              }
              image {
                ...image
                __typename
              }
              properties {
                collectionSubtitle
                __typename
              }
              status
              title
              __typename
            }
            faceout {
              id
              image {
                ...image
                __typename
              }
              color
              status
              variantStacks {
                id
                size
                variant {
                  ...variant
                  __typename
                }
                __typename
              }
              __typename
            }
            __typename
          }
          swatch {
            id
            image {
              ...image
              __typename
            }
            __typename
          }
          title
          variantStacks {
            id
            size
            variant {
              ...variant
              __typename
            }
            __typename
          }
          waitlistType
          waitlistTypeDisplay
          __typename
        }
        __typename
      }
      __typename
    }
    image {
      ...image
      __typename
    }
    fabric {
      id
      attributes
      careInstructions {
        id
        description
        __typename
      }
      composition
      description
      evergreen
      icon {
        id
        title
        svg
        __typename
      }
      parent {
        id
        __typename
      }
      title
      url
      __typename
    }
    pdp {
      id
      careInstructions {
        id
        description
        __typename
      }
      customDetailDrawer {
        description
        title
        __typename
      }
      instagrams {
        id
        instagram {
          ...instagram
          __typename
        }
        __typename
      }
      details
      relatedCta
      relatedUrl
      subtitle
      productKnowledgeSet {
        id
        title
        productKnowledges {
          id
          title
          description
          type
          __typename
        }
        __typename
      }
      __typename
    }
    properties {
      id
      description
      fitRatingAvg
      gender
      questionsCount
      ratingAvg
      recommendation {
        id
        placementId
        title
        __typename
      }
      reviewable
      reviewCount
      reviewWithPhotoCount
      sizeComponent
      sizeGuide
      sizingRecommendation
      __typename
    }
    shopifyId
    split
    soldOutStatus
    title
    __typename
  }
}
